import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import RoleManagement from '../pages/RoleManagement';
import SessionMonitor from '../pages/SessionMonitor';
import SecurityAuditLog from '../pages/SecurityAuditLog';
import './Dashboard.css';

const DashboardPage = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showAddRoleModal, setShowAddRoleModal] = useState(false);
  const [roleName, setRoleName] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    // Check if user is logged in
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user) {
      // If no user is found in localStorage, redirect to login page
      navigate("/login");
    } else {
      setIsAuthenticated(true); // User is authenticated
    }
  }, [navigate]);

  const handleLogout = () => {
    // Remove user data from localStorage
    localStorage.removeItem("user");
    // Redirect to login page
    navigate("/login");
  };

  const handleAddRole = () => {
    // Handle adding a new role (could be an API call or local state update)
    console.log("New Role Added:", roleName);
    // Close the modal after adding the role
    setShowAddRoleModal(false);
    setRoleName(""); // Reset role name
  };

  const handleRoleNameChange = (e) => {
    setRoleName(e.target.value);
  };

  const handleCancel = () => {
    setShowAddRoleModal(false); // Close the modal without adding a role
    setRoleName(""); // Reset role name
  };

  if (!isAuthenticated) {
    return null; // Don't render the dashboard content if the user is not authenticated
  }

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>SecureOps</h1>
        <div className="security-status">
          Security Status: <span>Low</span>
        </div>
        <button className="logout-btn" onClick={handleLogout}>
          Log Out
        </button>
      </header>
      <main>
        <section className="top-section">
          <RoleManagement />
          <SessionMonitor />
        </section>
        <section className="bottom-section">
          <SecurityAuditLog />
        </section>
      </main>
      {/* Add Role Button */}
      <button className="add-role-btn" onClick={() => setShowAddRoleModal(true)}>
        Add Role
      </button>

      {/* Add Role Modal */}
      {showAddRoleModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Add New Role</h2>
            <input
              type="text"
              value={roleName}
              onChange={handleRoleNameChange}
              placeholder="Enter Role Name"
            />
            <div className="modal-actions">
              <button onClick={handleAddRole}>Add Role</button>
              <button onClick={handleCancel}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default DashboardPage;
