import React, { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import RoleManagement from '../pages/RoleManagement';
import SessionMonitor from '../pages/SessionMonitor';
import SecurityAuditLog from '../pages/SecurityAuditLog';
import './Dashboard.css'; // Import the CSS for styling

const DashboardPage = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate();

  // Refs for the canvas elements
  const canvasRef1 = useRef(null);
  const canvasRef2 = useRef(null);

  useEffect(() => {
    // Check if user is logged in
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user) {
      // If no user is found in localStorage, redirect to login page
      navigate("/login");
    } else {
      setIsAuthenticated(true); // User is authenticated
    }
  }, [navigate]);

  const handleLogout = () => {
    // Remove user data from localStorage
    localStorage.removeItem("user");
    // Redirect to login page
    navigate("/login");
  };

  useEffect(() => {
    if (isAuthenticated) {
      const ctx1 = canvasRef1.current.getContext("2d");
      const ctx2 = canvasRef2.current.getContext("2d");

      // Draw on the first canvas
      if (ctx1) {
        ctx1.fillStyle = "#3498db";
        ctx1.fillRect(10, 10, 150, 100); // Simple blue rectangle for demo
        ctx1.font = "16px Arial";
        ctx1.fillStyle = "#fff";
        ctx1.fillText("Session 1", 60, 50);
      }

      // Draw on the second canvas
      if (ctx2) {
        ctx2.fillStyle = "#e74c3c";
        ctx2.fillRect(10, 10, 150, 100); // Simple red rectangle for demo
        ctx2.font = "16px Arial";
        ctx2.fillStyle = "#fff";
        ctx2.fillText("Session 2", 60, 50);
      }
    }
  }, [isAuthenticated]); // Runs whenever the component is authenticated

  if (!isAuthenticated) {
    return null; // Don't render the dashboard content if the user is not authenticated
  }

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h1>SecureOps</h1>
        <div className="security-status">
          Security Status: <span>Low</span>
        </div>
        <button className="logout-btn" onClick={handleLogout}>
          Log Out
        </button>
      </header>
      <main>
        <section className="top-section">
          <RoleManagement />
          <SessionMonitor />
        </section>
        <section className="bottom-section">
          <SecurityAuditLog />
        </section>
        <section className="canvas-container">
          <canvas ref={canvasRef1}></canvas> {/* First canvas */}
          <canvas ref={canvasRef2}></canvas> {/* Second canvas */}
        </section>
      </main>
    </div>
  );
};

export default DashboardPage;
