import React, { useEffect, useRef } from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import './MyChartComponent.css';

// Register components needed by Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

const MyChartComponent = ({ chartData, chartType = "pie", title = "My Chart" }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    if (canvasRef.current) {
      const ctx = canvasRef.current.getContext("2d");

      // Destroy any existing chart to prevent errors
      if (window.myChart) {
        window.myChart.destroy();
      }

      // Create a new chart
      window.myChart = new ChartJS(ctx, {
        type: chartType, // Dynamic chart type
        data: chartData,
        options: {
          responsive: true,
          maintainAspectRatio: false, // Ensures the chart fits properly
          plugins: {
            title: {
              display: true,
              text: title,
              font: {
                size: 18,
              },
            },
            tooltip: {
              backgroundColor: "#fff",
              borderColor: "#ddd",
              borderWidth: 1,
              titleColor: "#000",
              bodyColor: "#333",
            },
            legend: {
              position: "top",
              labels: {
                color: "#333",
                font: {
                  size: 14,
                },
              },
            },
          },
        },
      });
    }

    // Cleanup function
    return () => {
      if (window.myChart) {
        window.myChart.destroy();
      }
    };
  }, [chartData, chartType, title]);

  return (
    <div className="chart-container">
      <canvas ref={canvasRef}></canvas>
    </div>
  );
};

export default MyChartComponent;
