import React, { useState } from "react";
import './RoleManagement.css';

const RoleManagement = () => {
  // Initial roles data with description, users, and permissions
  const [roles, setRoles] = useState([
    { role: "Admin", description: "Full system access", users: 5, permissions: ["Read", "Write", "Execute"] },
    { role: "Security Analyst", description: "Monitor and analyze security events", users: 12, permissions: ["Read", "Write"] },
    { role: "Incident Responder", description: "Respond to security incidents", users: 8, permissions: ["Read"] },
  ]);

  // State to handle new role name input
  const [newRoleName, setNewRoleName] = useState("");

  // Function to handle adding a new role
  const addNewRole = () => {
    if (newRoleName.trim()) {
      const newRole = {
        role: newRoleName,
        description: "Description for " + newRoleName,
        users: 0, // Default to 0 users
        permissions: ["Read"], // Default permissions
      };
      setRoles([...roles, newRole]); // Add new role to the list
      setNewRoleName(""); // Clear the input field
    }
  };

  return (
    <div className="role-management">
      <h2>Role Management</h2>

      {/* Input and button for adding new role */}
      <div className="add-role-section">
        <input
          type="text"
          value={newRoleName}
          onChange={(e) => setNewRoleName(e.target.value)}
          placeholder="Enter new role name"
        />
        <button className="add-role-button" onClick={addNewRole}>+ Add Role</button>
      </div>

      {/* Roles table */}
      <table>
        <thead>
          <tr>
            <th>Role</th>
            <th>Description</th>
            <th>Users</th>
            <th>Permissions</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {roles.map((role, index) => (
            <tr key={index}>
              <td>{role.role}</td>
              <td>{role.description}</td>
              <td>{role.users}</td>
              <td>
                {role.permissions.map((perm, i) => (
                  <span key={i} className={`permission ${perm.toLowerCase()}`}>
                    {perm}
                  </span>
                ))}
              </td>
              <td>
                <button className="edit-btn">Edit</button>
                <button className="delete-btn">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default RoleManagement;
